protected static int toDigit(char ch, int index) throws DecoderException {
int digit = Character.digit(ch, 16);
if (digit == -1) {
return digit;
}

public static byte[] encodeBase64(byte[] binaryData, boolean isChunked, boolean urlSafe) {
