}

public int read() throws IOException {
if (bytesRemaining <= 0) {
int ret = in.read();
if (ret >= 0) {
checksum.update(ret);
--bytesRemaining;
if (bytesRemaining == 0 && expectedChecksum != checksum.getValue()) {
return ret;
}

public int read(byte[] b, int off, int len) throws IOException {
int ret = in.read(b, off, len);
if (ret >= 0) {
checksum.update(b, off, ret);
bytesRemaining -= ret;
if (bytesRemaining <= 0 && expectedChecksum != checksum.getValue()) {
