String h = "<html><head><style /><meta name=foo></head><body>One</body></html>";
Document doc = Jsoup.parse(h);
assertEquals("<html><head><style></style><meta name=\"foo\"></head><body>One</body></html>", TextUtil.stripNewlines(doc.html()));
}

