@Test public void preSkipsFirstNewline() {
Document doc = Jsoup.parse("<pre>

One
Two
</pre>");
Element pre = doc.selectFirst("pre");
assertEquals("One
Two", pre.text());
assertEquals("
One
Two
", pre.wholeText());
}

