public void testLang315() {
StopWatch watch = new StopWatch();
watch.start();
try {Thread.sleep(200);} catch (InterruptedException ex) {}
watch.suspend();
long suspendTime = watch.getTime();
try {Thread.sleep(200);} catch (InterruptedException ex) {}
watch.stop();
long totalTime = watch.getTime();
assertTrue( suspendTime == totalTime );
}

