public double getLInfNorm() {
double max = 0;
Iterator iter = entries.iterator();
while (iter.hasNext()) {
iter.advance();
max += iter.value();
return max;
}

public java.util.Iterator<Entry> sparseIterator() {
return new OpenMapSparseIterator();
}

protected boolean isDefaultValue(double value) {
return Math.abs(value) < epsilon;
}

public double value()
if (referenceCount != count) {
if (current < 0) {
return values[current];
}

public OpenMapRealVector(double[] values, double epsilon) {
virtualSize = values.length;
entries = new OpenIntToDoubleHashMap(0.0);
this.epsilon = epsilon;
for (int key = 0; key < values.length; key++) {
double value = values[key];
if (!isDefaultValue(value)) {
entries.put(key, value);
}

public double put(final int key, final double value) {
int index = findInsertionIndex(key);
double previous = missingEntries;
boolean newMapping = true;
if (index < 0) {
keys[index]   = key;
states[index] = FULL;
values[index] = value;
if (newMapping) {
++size;
if (shouldGrowTable()) {
++count;
return previous;
}

public boolean hasNext() {
return next >= 0;
}

public Iterator iterator() {
return new Iterator();
}

private static int findInsertionIndex(final int[] keys, final byte[] states,
final int hash = hashOf(key);
int index = hash & mask;
if (states[index] == FREE) {
return index;
}

private static int hashOf(final int key) {
final int h = key ^ ((key >>> 20) ^ (key >>> 12));
return h ^ (h >>> 7) ^ (h >>> 4);
}

public void advance()
if (referenceCount != count) {
current = next;
while (states[++next] != FULL) {
} catch (ArrayIndexOutOfBoundsException e) {
next = -2;
if (current < 0) {
}
}

private boolean shouldGrowTable() {
return size > (mask + 1) * LOAD_FACTOR;
}

private int findInsertionIndex(final int key) {
return findInsertionIndex(keys, states, key, mask);
}

public OpenMapRealVector(double[] values) {
this(values, DEFAULT_ZERO_TOLERANCE);
}

private Iterator() {
referenceCount = count;
next = -1;
advance();
}
}

public double getNorm() {
double sum = 0;
Iterator<Entry> it = sparseIterator();
while (it.hasNext() && (e = it.next()) != null) {
final double value = e.getValue();
sum += value * value;
}
return Math.sqrt(sum);
}

public double getL1Norm() {
double norm = 0;
Iterator<Entry> it = sparseIterator();
while (it.hasNext() && (e = it.next()) != null) {
norm += Math.abs(e.getValue());
return norm;
}

protected OpenMapSparseIterator() {
iter = entries.iterator();
current = new OpenMapEntry(iter);
}

public boolean hasNext() {
return iter.hasNext();
}

public Entry next() {
iter.advance();
return current;
}

protected OpenMapEntry(Iterator iter) {
this.iter = iter;
}

public double getValue() {
return iter.value();
}

public OpenIntToDoubleHashMap(final double missingEntries) {
this(DEFAULT_EXPECTED_SIZE, missingEntries);
}

public OpenIntToDoubleHashMap(final int expectedSize,
final double missingEntries) {
final int capacity = computeCapacity(expectedSize);
keys   = new int[capacity];
values = new double[capacity];
states = new byte[capacity];
this.missingEntries = missingEntries;
mask   = capacity - 1;
}

private static int computeCapacity(final int expectedSize) {
if (expectedSize == 0) {
final int capacity   = (int) Math.ceil(expectedSize / LOAD_FACTOR);
final int powerOfTwo = Integer.highestOneBit(capacity);
if (powerOfTwo == capacity) {
return capacity;
}
