public double getL1Norm() {
double sum = 0;
for (double a : data) {
sum += Math.abs(a);
return sum;
}

public double getLInfNorm() {
double max = 0;
for (double a : data) {
max += Math.max(max, Math.abs(a));
return max;
}

public double getNorm() {
double sum = 0;
for (double a : data) {
sum += a * a;
return Math.sqrt(sum);
}
