protected AbstractWell(final int k, final int m1, final int m2, final int m3, final int[] seed) {
final int w = 32;
final int r = (k + w - 1) / w;
this.v      = new int[r];
this.index  = 0;
iRm1 = new int[r];
iRm2 = new int[r];
i1   = new int[r];
i2   = new int[r];
i3   = new int[r];
for (int j = 0; j < r; ++j) {
iRm1[j] = (j + r - 1) % r;
iRm2[j] = (j + r - 2) % r;
i1[j]   = (j + m1)    % r;
i2[j]   = (j + m2)    % r;
i3[j]   = (j + m3)    % r;
setSeed(seed);
}

