public Object handle(Invocation invocation) throws Throwable {
if (invocationContainerImpl.hasAnswersForStubbing()) {
VerificationMode verificationMode = mockingProgress.pullVerificationMode();
InvocationMatcher invocationMatcher = matchersBinder.bindMatchers(mockingProgress.getArgumentMatcherStorage(),
mockingProgress.validateState();
if (verificationMode != null) {
VerificationDataImpl data = new VerificationDataImpl(invocationContainerImpl.getInvocations(), invocationMatcher);
verificationMode.verify(data);
return null;
invocationContainerImpl.setInvocationForPotentialStubbing(invocationMatcher);
OngoingStubbingImpl<T> ongoingStubbing = new OngoingStubbingImpl<T>(invocationContainerImpl);
mockingProgress.reportOngoingStubbing(ongoingStubbing);
StubbedInvocationMatcher stubbedInvocation = invocationContainerImpl.findAnswerFor(invocation);
if (stubbedInvocation != null) {
Object ret = mockSettings.getDefaultAnswer().answer(invocation);
invocationContainerImpl.resetInvocationForPotentialStubbing(invocationMatcher);
return ret;
}

public <T> OngoingStubbing<T> when(T methodCall) {
mockingProgress.stubbingStarted();
return (OngoingStubbing) stub();
}

public <T> T mock(Class<T> classToMock, MockSettings mockSettings) {
T mock = mockUtil.createMock(classToMock, (MockSettingsImpl) mockSettings);
mockingProgress.mockingStarted(mock, classToMock, mockSettings);
return mock;
}

public MockHandler(MockSettingsImpl mockSettings) {
this.mockSettings = mockSettings;
this.mockingProgress = new ThreadSafeMockingProgress();
this.matchersBinder = new MatchersBinder();
this.invocationContainerImpl = new InvocationContainerImpl(mockingProgress);
}

public IOngoingStubbing stub() {
IOngoingStubbing stubbing = mockingProgress.pullOngoingStubbing();
if (stubbing == null) {
return stubbing;
}

public <T> T verify(T mock, VerificationMode mode) {
if (mock == null) {
} else if (!mockUtil.isMock(mock)) {
mockingProgress.verificationStarted(mode);
return mock;
}
